				{ "EMDDetailsbyDocumentNumberRS.EMDDocumentDetail.Passenger.UnstructuredFareCalcInfo", "SELECT UNF.* FROM UNSTRUCTURED_FARE_CALC_INFO UNF, TRAVEL_DOCUMENT TD WHERE ( (TD.PRIMARY_DOCUMENT_NBR = $DocumentNumber) ) AND (TD.PRIMARY_AIRLINE_CD = $PrimaryAirlineCd) AND (TD.PRIMARY_ISSUE_DT = $PrimaryIssueDate) AND TD.PAX_ID = UNF.PAX_ID ORDER BY UNF.PAX_ID, UNF.UFCI_SEQ" },
