				{ "EMDDetailsbyDocumentNumberRS.EMDDocumentDetail.TransactionHistory.RefundExchange.RefundExchangeCoupon", "SELECT REC.* FROM REFUND_EXCHANGE_COUPONS REC, REFUND_EXCHANGE RE, TRANSACTION_HISTORY TH WHERE (TH.PRIMARY_AIRLINE_CD = $PrimaryAirlineCd) AND (TH.PRIMARY_DOCUMENT_NBR = $DocumentNumber) AND (TH.PRIMARY_ISSUE_DT = $PrimaryIssueDate) AND TH.TXN_HISTORY_ID = RE.TXN_HISTORY_ID AND RE.TXN_HISTORY_ID = REC.TXN_HISTORY_ID ORDER BY REC.PRIMARY_AIRLINE_CD, REC.PRIMARY_DOCUMENT_NBR, REC.PRIMARY_ISSUE_DT, REC.BOOKLET_DOCUMENT_NBR, REC.CPN_NBR, REC.TXN_HISTORY_ID" },
