				{ "EMDDetailsbyDocumentNumberRS.EMDDocumentDetail.TransactionHistory.RefundExchange.RefundTaxFeeChargesGroup.RefundTaxFeeCharges", "SELECT RETFC.* FROM RFND_EXCH_TAX_FEE_CHARGES RETFC, RFND_EXCH_TAX_FEE_CHARGES_GRP RETFCG, REFUND_EXCHANGE RE, TRANSACTION_HISTORY TH WHERE (TH.PRIMARY_AIRLINE_CD = $PrimaryAirlineCd) AND (TH.PRIMARY_DOCUMENT_NBR = $DocumentNumber) AND (TH.PRIMARY_ISSUE_DT = $PrimaryIssueDate) AND TH.TXN_HISTORY_ID = RE.TXN_HISTORY_ID AND RE.RFND_EXCH_EMD_TFC_GROUP_ID = RETFC.RFND_EXCH_TFC_GROUP_ID AND RETFC.RFND_EXCH_TFC_GROUP_ID = RETFCG.RFND_EXCH_TFC_GROUP_ID ORDER BY RETFC.RFND_EXCH_TFC_GROUP_ID, RETFC.RFND_EXCH_TFC_SEQ" },
